mapper.update:
UPDATE users 
SET phone_number = #{phoneNumber} 
WHERE email = #{username}

mapper.select:
SELECT email 
FROM users 
WHERE REPLACE(phone_number, '-', '') = #{phoneNumber}

SELECT COUNT(*) 
FROM users 
WHERE email = #{email} 
  AND REPLACE(phone_number, '-', '') = #{phoneNumber}

mapper.update:
UPDATE users 
SET password = #{password} 
WHERE email = #{email}

mapper.insert:
INSERT INTO social_accounts (
    social_id, connected_at, provider, provider_id, user_id
)
VALUES (
    #{socialId}, NOW(), #{provider}, #{providerId}, #{userId}
)
