mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM user_favorites
    WHERE user_id = #{userId}
      AND product_id = #{productId}
)

mapper.delete:
DELETE
FROM user_favorites
WHERE user_id = #{userId}
  AND product_id = #{productId}

mapper.select:
SELECT COUNT(*)
FROM user_favorites
WHERE product_id = #{productId}
